// import 'uppy/dist/uppy.min.css'

// import {
//   Core,
// } from 'uppy'

import Uppy from '@uppy/core'
import FileInput from '@uppy/file-input'
import ThumbnailGenerator from '@uppy/thumbnail-generator'
import ProgressBar from '@uppy/progress-bar'
import Informer from '@uppy/informer'
// const Dashboard = require("@uppy/dashboard")
const XHRUpload = require("@uppy/xhr-upload")
const GoogleDrive = require("@uppy/google-drive")

function fileUpload(fileInput) {
  const hiddenInput = document.querySelector('.upload-data'),
        filePreview = document.querySelector('.file-preview'),
        form = $(fileInput).parents('form'),
        submitButton = document.getElementById('button-form-uppy'),
        formGroup = fileInput.parentNode;
  var attachment_url    = window.location.origin + $('#attachment_url').data('url');

  var authenticity_token = $('meta[name="csrf-token"]').attr('content');
  var compiledToken, compiledLink;

  if (attachment_url.includes('?')) {
    compiledToken = `&authenticity_token=${authenticity_token}`
  } else {
    compiledToken = `?authenticity_token=${authenticity_token}`
  }

  compiledLink = `${attachment_url}${compiledToken}`;

  // remove our file input in favour of Uppy's
  formGroup.removeChild(fileInput)

  const uppy = new Uppy({
      autoProceed: true,
    })
    .use(FileInput, {
      target: formGroup,
    })
    .use(Informer, {
      target: formGroup,
    })
    .use(ProgressBar, {
      target: filePreview.parentNode,
    })
    .use(XHRUpload, {
      endpoint: compiledLink,
      fieldName: 'files[]',
    })

  uppy.on('upload-success', (file, response) => {
    $('.file-preview').append(response.body.files_previews);
  });

  uppy.on('upload-error', (file, error, response) => {
    submitButton.removeAttribute("disabled")
    submitButton.innerHTML = "Save"
  })
}

export default fileUpload
