$(document).ready(function(){
  const sidebar = document.querySelector('.sidebar')
  const toggle = document.querySelector('.navbar-toggle');
  const userProfileDropdown = document.querySelector('.user-profile')
  const btnClose = document.querySelector('.btn-close')

  toggle.addEventListener('click',function(){
    sidebar.classList.toggle('active')
  })

  userProfileDropdown.addEventListener('click', function(){
    document.querySelector('.user-profile-menu').classList.toggle('hidden')
  })

  btnClose.addEventListener('click', function(){
    sidebar.classList.toggle('active')
  })

  $('#domain-access-field').change(function(){
    var data_form = $("#search-contact-form").serialize()
    $.ajax({
      url: $(this).data('url'),
      type: "GET",
      dataType:"script",
      data: data_form,
    })
  });

  $('#admin-domain-access-field').change(function(){
    var data_form = $("#search-admin-form").serialize()
    $.ajax({
      url: $(this).data('url'),
      type: "GET",
      dataType:"script",
      data: data_form,
    })
  });

  $('.service-field').select2({
    placeholder: "Please Choose",
    width: '100%',
  });
})
