import {
  Core,
  FileInput,
  Informer,
  ProgressBar,
  XHRUpload,
} from 'uppy'

document.addEventListener('DOMContentLoaded', () => {
  const fileInput = document.querySelector('#my-file-input')
  // console.log('-----a-------')
  // console.log(fileInput)
  // console.log('-----a-------')

  fileInput.addEventListener('change', (event) => {
    const files = Array.from(event.target.files)

    files.forEach((file) => {
    console.log(files)
      try {
        uppy.addFile({
          source: 'file input',
          name: file.name,
          type: file.type,
          data: file,
        })
      } catch (err) {
        if (err.isRestriction) {
          // handle restrictions
          console.log('Restriction error:', err)
        } else {
          // handle other errors
          console.error(err)
        }
      }
    })
  })
});

const uppy = Core({
  autoProceed: true,
})
